
public class HelloWorld {

	public static void main(String[] args) {
		/* TODO Auto-generated method stub
		 * test
		 */
		System.out.println("Hello"); //Hello World ist nicht mehr auf einer Zeile
		System.out.println("World");

		System.out.println(" ");
		
		System.out.print("Hello"); //Hello World ist zusammen auf einer Zeile geschrieben
		System.out.print("World");

		System.out.println(" ");
		System.out.println(" ");
		
		System.out.print("Hello\n"); //Hello ist über World
		System.out.print("World");
		
		System.out.println(" ");
		System.out.println(" ");
		
		System.out.print("\tHello\n"); //Hello ist über World und um ein TAB versetzt
		System.out.print("World");


	}

}
